//SPDX-License-Identifier: MIT

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

import { IKeyHashAdapterV2 } from "./interfaces/IKeyHashAdapterV2.sol";
import { Bytes32ArrayUtils } from "./lib/Bytes32ArrayUtils.sol";

pragma solidity ^0.8.18;

contract ManagedKeyHashAdapterV2 is Ownable, IKeyHashAdapterV2 {

    using Bytes32ArrayUtils for bytes32[];

    /* ============ Events ============ */
    event MailServerKeyHashAdded(bytes32 mailserverKeyHash);
    event MailServerKeyHashRemoved(bytes32 mailserverKeyHash);

    /* ============ State Variables ============ */

    mapping(bytes32 => bool) public isMailServerKeyHash;
    bytes32[] public mailServerKeyHashes;

    /* ============ Constructor ============ */

    constructor(
        bytes32[] memory _mailServerKeyHashes
    )
        Ownable()
    {
        for (uint256 i = 0; i < _mailServerKeyHashes.length; i++) {
            bytes32 mailserverKeyHash = _mailServerKeyHashes[i];
            require(!isMailServerKeyHash[mailserverKeyHash], "Key hash already added");

            isMailServerKeyHash[mailserverKeyHash] = true;
            mailServerKeyHashes.push(mailserverKeyHash);
        }
    }

    /* ============ External Functions ============ */

    function addMailServerKeyHash(bytes32 _mailserverKeyHash) external onlyOwner {
        require(!isMailServerKeyHash[_mailserverKeyHash], "Key hash already added");

        isMailServerKeyHash[_mailserverKeyHash] = true;
        mailServerKeyHashes.push(_mailserverKeyHash);

        emit MailServerKeyHashAdded(_mailserverKeyHash);
    }

    function removeMailServerKeyHash(bytes32 _mailserverKeyHash) external onlyOwner {
        require(isMailServerKeyHash[_mailserverKeyHash], "Key hash not added");

        isMailServerKeyHash[_mailserverKeyHash] = false;
        mailServerKeyHashes.removeStorage(_mailserverKeyHash);

        emit MailServerKeyHashRemoved(_mailserverKeyHash);
    }

    /* ============ External Getter Functions ============ */

    function getMailServerKeyHashes() external view override returns (bytes32[] memory) {
        return mailServerKeyHashes;
    }
}

contracts/external/NullifierRegistry.sol.content:
//SPDX-License-Identifier: MIT

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

import { AddressArrayUtils } from "./lib/AddressArrayUtils.sol";
import { INullifierRegistry } from "./interfaces/INullifierRegistry.sol";

pragma solidity ^0.8.18;

contract NullifierRegistry is Ownable, INullifierRegistry {

    using AddressArrayUtils for address[];

    /* ============ Events ============ */
    event NullifierAdded(bytes32 nullifier, address indexed writer);
    event WriterAdded(address writer);
    event WriterRemoved(address writer);

    /* ============ Modifiers ============ */
    modifier onlyWriter() {
        require(isWriter[msg.sender], "Only addresses with write permissions can call");
        _;
    }

    /* ============ State Variables ============ */
    mapping(bytes32 => bool) public isNullified;
    mapping(address => bool) public isWriter;
    address[] public writers;

    /* ============ Constructor ============ */
    constructor() Ownable() {}

    /* ============ External Functions ============ */

    /**
     * ONLY WRITER: Only addresses with permission to write to this contract can call. Stores a nullifier for an email.
     *
     * @param _nullifier    The nullifier to store
     */
    function addNullifier(bytes32 _nullifier) external onlyWriter {
        require(!isNullified[_nullifier], "Nullifier already exists");

        isNullified[_nullifier] = true;

        emit NullifierAdded(_nullifier, msg.sender);
    }

    /* ============ Admin Functions ============ */

    /**
     * ONLY OWNER: Add address that has write permissions to the registry. Writer must not have been previously added.
     *
     * @param _newWriter    The nullifier to store
     */
    function addWritePermission(address _newWriter) external onlyOwner {
        require(!isWriter[_newWriter], "Address is already a writer");

        isWriter[_newWriter] = true;
        writers.push(_newWriter);

        emit WriterAdded(_newWriter);
    }

    /**
     * ONLY OWNER: Remove address that has write permissions to the registry. Writer must have been previously added.
     *
     * @param _removedWriter    The nullifier to store
     */
    function removeWritePermission(address _removedWriter) external onlyOwner {
        require(isWriter[_removedWriter], "Address is not a writer");

        isWriter[_removedWriter] = false;
        writers.removeStorage(_removedWriter);

        emit WriterRemoved(_removedWriter);
    }

    /* ============ External View Functions ============ */

    function getWriters() external view returns(address[] memory) {
       return writers;
    }
}

contracts/external/processors/EmailBaseP.content:
//SPDX-License-Identifier: MIT

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

import { IKeyHashAdapterV2 } from "../interfaces/IKeyHashAdapterV2.sol";
import { INullifierRegistry } from "../interfaces/INullifierRegistry.sol";

pragma solidity ^0.8.18;

contract EmailBaseProcessor is Ownable {

    /* ============ Modifiers ============ */
    modifier onlyExchange() {
        require(msg.sender == exchange, "Only exchange can call this function");
        _;
    }

    /* ============ State Variables ============ */
    address public immutable exchange;
    INullifierRegistry public nullifierRegistry;
    bytes public emailFromAddress;
    uint256 public timestampBuffer;

    /* ============ Constructor ============ */
    constructor(
        address _exchange,
        INullifierRegistry _nullifierRegistry,
        string memory _emailFromAddress,
        uint256 _timestampBuffer
    )
        Ownable()
    {
        exchange = _exchange;
        nullifierRegistry = _nullifierRegistry;
        emailFromAddress = bytes(_emailFromAddress);
        timestampBuffer = _timestampBuffer;
    }

    /* ============ External Functions ============ */

    /**
     * @notice ONLY OWNER: Sets the from email address for validated emails. Check that email address is properly
     * padded (if necessary). Padding will be dependent on if unpacking functions cut trailing 0s or not.
     *
     * @param _emailFromAddress    The from email address for validated emails, MUST BE PROPERLY PADDED
     */
    function setEmailFromAddress(string memory _emailFromAddress) external onlyOwner {
        emailFromAddress = bytes(_emailFromAddress);
    }

    /**
     * @notice ONLY OWNER: Sets the timestamp buffer for validated emails. This is the amount of time in seconds
     * that the timestamp can be off by and still be considered valid. Necessary to build in flexibility with L2
     * timestamps.
     *
     * @param _timestampBuffer    The timestamp buffer for validated emails
     */
    function setTimestampBuffer(uint256 _timestampBuffer) external onlyOwner {
        timestampBuffer = _timestampBuffer;
    }

    /* ============ External Getters ============ */

    function getEmailFromAddress() external view returns (bytes memory) {
        return emailFromAddress;
    }


    /* ============ Internal Functions ============ */

    function _validateAndAddNullifier(bytes32 _nullifier) internal {
        require(!nullifierRegistry.isNullified(_nullifier), "Nullifier has already been used");
        nullifierRegistry.addNullifier(_nullifier);
    }
}

contracts/external/processors/ProxyBaseP.content:
//SPDX-License-Identifier: MIT
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { Claims } from "@reclaimprotocol/verifier-solidity-sdk/contracts/Claims.sol";

import { AddressArrayUtils } from "../lib/AddressArrayUtils.sol";
import { StringArrayUtils } from "../lib/StringArrayUtils.sol";
import { INullifierRegistry } from "../interfaces/INullifierRegistry.sol";
import { IProxyBaseProcessor } from "../interfaces/IProxyBaseProcessor.sol";
import { ClaimVerifier } from "../ClaimVerifier.sol";

pragma solidity ^0.8.18;

contract ProxyBaseProcessor is IProxyBaseProcessor, Ownable {

    using AddressArrayUtils for address[];
    using StringArrayUtils for string[];

    /* ============ State Variables ============ */
    mapping(address => bool) public isWitness;
    address[] public witnesses;
    mapping(string => bool) public isProviderHash;
    string[] public providerHashes;                         // Set of provider hashes that these proofs should be for
    INullifierRegistry public immutable nullifierRegistry;

    /* ============ Events ============ */
    event WitnessAdded(address witness);
    event WitnessRemoved(address witness);
    event ProviderHashAdded(string providerHash);
    event ProviderHashRemoved(string providerHash);

    /* ============ Constructor ============ */
    constructor(
        INullifierRegistry _nulliferRegistry,
        string[] memory _providerHashes
    )
        Ownable()
    {
        nullifierRegistry = _nulliferRegistry;

        for (uint256 i = 0; i < _providerHashes.length; i++) {
            require(!isProviderHash[_providerHashes[i]], "Provider hash already added");
            isProviderHash[_providerHashes[i]] = true;
            providerHashes.push(_providerHashes[i]);

            emit ProviderHashAdded(_providerHashes[i]);
        }
    }

    /* ============ Admin Functions ============ */

    /**
     * ONLY OWNER: Add witness address. Witness must not have been previously added.
     *
     * @param _newWitness    Address of the new witness
     */
    function addWitness(address _newWitness) external onlyOwner {
        require(!isWitness[_newWitness], "Address is already a witness");

        isWitness[_newWitness] = true;
        witnesses.push(_newWitness);

        emit WitnessAdded(_newWitness);
    }

    /**
     * ONLY OWNER: Remove witness address. Witness must have been previously added.
     *
     * @param _removeWitness    Address of witness to be removed
     */
    function removeWitness(address _removeWitness) external onlyOwner {
        require(isWitness[_removeWitness], "Address is not a witness");

        delete isWitness[_removeWitness];
        witnesses.removeStorage(_removeWitness);

        emit WitnessRemoved(_removeWitness);
    }

    /**
     * ONLY OWNER: Add provider hash string. Provider hash must not have been previously added.
     *
     * @param _newProviderHash    New provider hash to be added
     */
    function addProviderHash(string memory _newProviderHash) external onlyOwner {
        require(!isProviderHash[_newProviderHash], "Provider hash already added");

        isProviderHash[_newProviderHash] = true;
        providerHashes.push(_newProviderHash);

        emit ProviderHashAdded(_newProviderHash);
    }

    /**
     * ONLY OWNER: Remove provider hash string. Provider hash must have been previously added.
     *
     * @param _removeProviderHash    Provider hash to be removed
     */
    function removeProviderHash(string memory _removeProviderHash) external onlyOwner {
        require(isProviderHash[_removeProviderHash], "Provider hash not found");

        delete isProviderHash[_removeProviderHash];
        providerHashes.removeStorage(_removeProviderHash);

        emit ProviderHashRemoved(_removeProviderHash);
    }

    /* ============ Public Functions ============ */

    /**
     * Verify proof generated by witnesses. Claim is constructed by hashing claimInfo (provider, context, parameters)
     * to get the identifier. And then signing on (identifier, owner, timestamp, epoch) to get claim signature. 
     * This function verifies a claim by performing the following checks on the claim
     * - Calculates the hash of the claimInfo and checks if it matches the identifier in the claim
     * - Checks if the signatures are valid and from the witnesses
     * This function reverts if
     * - No signatures are found on the proof
     * - Number of signatures is not equal to the number of witnesses
     * - ClaimInfo hash does not match the identifier in the claim
     * - Signatures are invalid (not from the witnesses)
     * 
     * DEV NOTE: This function does NOT validate that the claim provider hash is valid. That is the 
     * responsibility of the caller.
     * 
     * Parts of the code are adapted from: https://basescan.org/address/0x7281630e4346dd4c0b7ae3b4689c1d0102741410#code
     *    
     * @param proof                 Proof to be verified
     */
    function verifyProofSignatures(Proof memory proof) public view returns (bool) {

        // create signed claim using claimData and signature.
        require(proof.signedClaim.signatures.length > 0, "No signatures");
        Claims.SignedClaim memory signed = Claims.SignedClaim(
            proof.signedClaim.claim,
            proof.signedClaim.signatures
        );

        // check if the hash from the claimInfo is equal to the infoHash in the claimData
        bytes32 hashed = Claims.hashClaimInfo(proof.claimInfo);
        require(proof.signedClaim.claim.identifier == hashed, "ClaimInfo hash doesn't match");

        // Recover signers of the signed claim
        address[] memory signedWitnesses = Claims.recoverSignersOfSignedClaim(signed);

        // check if the number of signatures is equal to the number of witnesses
        require(
            signedWitnesses.length == witnesses.length,
            "Number of signatures not equal to number of witnesses"
        );

        // Check signatures are from witnesses
        for (uint256 i = 0; i < signed.signatures.length; i++) {
            bool found = false;
            for (uint j = 0; j < witnesses.length; j++) {
                if (signedWitnesses[i] == witnesses[j]) {
                    found = true;
                    break;
                }
            }
            require(found, "Signature not appropriate");
        }

        return true;
    }


    /* ============ View Functions ============ */

    function getWitnesses() external view returns (address[] memory) {
        return witnesses;
    }

    function getProviderHashes() external view returns (string[] memory) {
        return providerHashes;
    }

    /* ============ Internal Functions ============ */

    function _validateProviderHash(string memory _providerHash) internal view returns (bool) {
        return isProviderHash[_providerHash];
    }

    function _validateAndAddNullifier(bytes[] memory _sigArray) internal {
        bytes32 nullifier = keccak256(abi.encode(_sigArray));
        require(!nullifierRegistry.isNullified(nullifier), "Nullifier has already been used");
        nullifierRegistry.addNullifier(nullifier);
    }
}

contracts/external/TicketmasterDataParsi.content:
//SPDX-License-Identifier: MIT

import { DateTime } from "./lib/DateTime.sol";

import { StringConversionUtils } from "./lib/StringConversionUtils.sol";
import { ClaimVerifier } from "./ClaimVerifier.sol";

pragma solidity ^0.8.18;

library TicketmasterDataParsing {

    using StringConversionUtils for string;

    /**
     * @notice Iterates through every character in the date string and splits the string at each dash, "T", or colon. Function will revert
     * if there are not 6 substrings formed from the split. The substrings are then converted to uints and passed to the DateTime lib
     * to get the unix timestamp. This function is SPECIFIC TO THE DATE FORMAT USED BY Ticketmaster, not suitable for use with other date
     * formats. Ticketmaster date format is: "YYYY-MM-DDTHH:MM:SS" and returns UTC timestamps.
     *
     * @param _dateString       Date string to be converted to a UTC timestamp
     */
    function _dateStringToTimestamp(string memory _dateString) internal pure returns (uint256 utcTimestamp) {
        string[6] memory extractedStrings;
        uint256 breakCounter;
        uint256 lastBreak;
        for (uint256 i = 0; i < bytes(_dateString).length; i++) {
            if (bytes(_dateString)[i] == 0x2d || bytes(_dateString)[i] == 0x3a || bytes(_dateString)[i] == 0x54) {
                extractedStrings[breakCounter] = _dateString.substring(lastBreak, i);
                lastBreak = i + 1;
                breakCounter++;
            }
        }
        // Add last substring to array
        extractedStrings[breakCounter] = _dateString.substring(lastBreak, bytes(_dateString).length);

        // Check that exactly 6 substrings were found (string is split at 5 different places)
        require(breakCounter == 5, "Invalid date string");

        utcTimestamp = DateTime.timestampFromDateTime(
            extractedStrings[0].stringToUint(0),    // year
            extractedStrings[1].stringToUint(0),    // month
            extractedStrings[2].stringToUint(0),    // day
            extractedStrings[3].stringToUint(0),    // hour
            extractedStrings[4].stringToUint(0),    // minute
            0                                       // we don't need to the second granularity
        );
    }

    /**
     * Extract event ID from URL by finding the last slash and taking the substring after it. We assume the
     * URL is formulated something like: {domainRoot}/.../event/EVENT_ID
     *
     * @param _url          URL to extract event ID from
     * @return              Event ID extracted from URL
     */
    function _extractEventIdFromUrl(string memory _url) internal pure returns (string memory) {
        string memory prefix = "event/";
        bytes memory urlBytes = bytes(_url);
        uint256 urlLength = urlBytes.length;

        uint256 startIndex = ClaimVerifier.findSubstringEndIndex(_url, prefix);
        // If the start index is the max value or the URL length, then the event ID was not found
        if (startIndex == type(uint256).max || startIndex == urlLength) {
            revert("Event ID not found in URL");
        }

        bytes memory contextMessage = new bytes(urlLength - startIndex);
        for (uint i = startIndex; i < urlLength; i++) {
            contextMessage[i - startIndex] = urlBytes[i];
        }
        return string(contextMessage);
    }

    /**
     * Parse tickets out of a ticket string of the format: '["ticket1", "ticket2", "ticket3"]'.
     * We start by getting the number of tickets in a string by counting the number of commas and adding 1.
     * Then we iterate through the string and find the ticket IDs by looking for the quotes. We need to extract
     * from quote to quote so we look for all even-numbered quotes then extract the string between that and the
     * previous quote index.
     *
     * @param _ticketString          Stringified array of tickets
     */
     function _parseTicketString(string memory _ticketString) internal pure returns (string[] memory ticketIds) {
        // Assume there is at least one ticket (which means array would not have a comma)
        // If there is no ticket then the transaction will end up reverting
        uint256 ticketCount = 1;
        for (uint256 i = 0; i < bytes(_ticketString).length; i++) {
            if (bytes(_ticketString)[i] == 0x2C) {
                ticketCount++;
            }
        }

        ticketIds = new string[](ticketCount);
        uint256 breakCounter;
        uint256 lastBreak;
        for (uint256 i = 0; i < bytes(_ticketString).length; i++) {
            if (bytes(_ticketString)[i] == 0x22) {
                breakCounter++;
                if (breakCounter % 2 == 0) {
                    ticketIds[(breakCounter/2)-1] = _ticketString.substring(lastBreak, i-1);
                }
                lastBreak = i + 1;
            }
        }
    }
}

